s = None
def setup(): size(100, 100, P2D) s = createShape() s.beginShape() s.vertex(0, 0) s.vertex(60, 0) s.vertex(60, 60) s.vertex(0, 60) s.endShape(CLOSE)
def draw(): translate(20, 20) for i in range(s.getVertexCount()): v = s.getVertex(i) v.x += random(-1, 1) v.y += random(-1, 1) s.setVertex(i, v) shape(s)
